    border: none;
    cursor: pointer;
    border-radius: 5px;
    text-transform: uppercase;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.3);
    font-family: "Comic Sans MS", cursive, sans-serif;
    transition: background-color 0.3s;
  }
  #check-button{
    margin-left: 650px;
  }

button:hover {
  background-color: #3daaff;
}
.back-link {
  display: inline-block;
  padding: 10px 20px;
  
  color: #fff;
  text-decoration: none;
  font-family: "Comic Sans MS", cursive, sans-serif;
  font-size: larger;
}



  </style>
</head>
<body>
<a href="games.php" class="back-link">Go Back</a>

<h2>Can you guess?</h2>
<div id="game-container">
  <div id="topic-container">  
  </div>
  <div id="image-container"></div>
  <div id="word-container"></div>
  <button id="check-button">Check</button>
  <button id="reset-button">Play again!</button>
</div>



<script>
  const apiUrl = 'http://127.0.0.1:5000/word/topic';
  let topics = {};
  let currentTopic = null;
  let currentWordIndex = 0;

  function startGame() {
    fetchTopics()
      .then(() => {
        renderTopics();
      })
      .catch((error) => {
        console.error('Failed to fetch topics:', error);
      });
  }

  function fetchTopics() {
    return fetch(apiUrl)
      .then((response) => response.json())
      .then((data) => {
        topics = data;
      });
  }

  function renderTopics() {
    const topicContainer = document.getElementById('topic-container');
    topicContainer.innerHTML = '';

    for (const topic in topics) {
      const topicButton = document.createElement('button');
      topicButton.textContent = topic;
      topicButton.addEventListener('click', () => {
        selectTopic(topic);
      });

      topicContainer.appendChild(topicButton);
    }
  }

  function selectTopic(topic) {
    const topicContainer = document.getElementById('topic-container');
    topicContainer.style.display = "none"
    currentTopic = topic;
    currentWordIndex = 0;
    renderImage();
    renderWord();
  }

  function renderImage() {
    const imageContainer = document.getElementById('image-container');
    imageContainer.innerHTML = '';

    if (currentTopic) {
      const currentTopicData = topics[currentTopic];
      const currentWord = currentTopicData[currentWordIndex + 1];

      if (currentWord && currentWord.image_url) {
        const image = document.createElement('img');
        image.src = currentWord.image_url;
        imageContainer.appendChild(image);
      } else {
        const errorMessage = document.createElement('p');
        
        
      }
    }
  }

  function renderWord() {
    const wordContainer = document.getElementById('word-container');
    wordContainer.innerHTML = '';

    if (currentTopic) {
      const currentTopicData = topics[currentTopic];
      const currentWord = currentTopicData[currentWordIndex + 1];

      if (currentWord) {
        const wordInput = document.createElement('input');
        wordInput.type = 'text';
        wordInput.id = 'word-input';
        wordContainer.appendChild(wordInput);
      } else {
        const topicContainer = document.getElementById('topic-container');
        topicContainer.style.display = ""
      }
    }
  }

  function checkWord() {
    const userInput = document.getElementById('word-input').value.toLowerCase();
    const currentTopicData = topics[currentTopic];
    const currentWord = currentTopicData[currentWordIndex + 1];
    const correctWord = currentWord ? currentWord.word.toLowerCase() : '';

    if (userInput === correctWord) {
      alert('Correct!');
      goToNextWord();
    } else {
      alert('Wrong!');
    }
  }

  function goToNextWord() {
    currentWordIndex++;

    const currentTopicData = topics[currentTopic];
    if (currentWordIndex >= currentTopicData.length) {
      currentTopic = null;
      currentWordIndex = 0;
      renderTopics();
      renderImage();
      renderWord();
    } else {
      renderImage();
      renderWord();
    }
  }

  function resetGame() {
    const topicContainer = document.getElementById('topic-container');
    topicContainer.style.display = ""
    currentTopic = null;
    currentWordIndex = 0;
    renderTopics();
    renderImage();
    renderWord();
  }

  document.getElementById('check-button').addEventListener('click', checkWord);
  document.getElementById('reset-button').addEventListener('click', resetGame);

  startGame();

</script>
</body>
</html>